document.addEventListener("DOMContentLoaded",(function(){document.querySelector(".header__burger").onclick=function(){document.querySelector(".header__burger").classList.toggle("active"),document.querySelector(".header__menu").classList.toggle("active"),document.querySelector(".header__menu-background-mobile").classList.toggle("active"),document.querySelector("body").classList.toggle("lock")},fetch("/data.json").then(i=>i.json()).then(renderDrivers)}));const renderDrivers=({drivers:i})=>{const n=document.getElementById("drivers-container");i.map((i,e)=>{const s=new Date(i.dateOfBirth),d=`${s.getFullYear()}/${s.getMonth()+1}/${s.getDate()}`,a=e%2!=0;n.insertAdjacentHTML("beforeend",`\n        <div class="driver-container-mobile">\n            <img src="${i.imageUrl}" class="driver-photo" />\n            <div class="driver-position">${i.position}</div>\n            <div class="line"></div>\n            <div class="driver-name">${i.firstName} ${i.lastName}</div>\n            <div class="description">\n                <div class="column">\n                    <div>Races: <span>${i.races}</span></div>\n                    <div>Pole-positions: <span>${i.polePositions}</span></div>\n                    <div>Podiums: <span>${i.podiums}</span></div>\n                </div>\n                <div class="column--last">\n                    <div>Wins: <span>${i.wins}</span></div>\n                    <div>Date of birth: <span>${d}</span></div>\n                    <div>Place of birth: <span>${i.city}, ${i.country}</span></div>\n                </div>\n            </div>\n        </div>\n      `),n.insertAdjacentHTML("beforeend",`\n          <div class="driver-container${a?"-reverse":""}">\n            <div class="line"></div>\n            <div class="driver-profile">\n              <img src="${i.imageUrl}" class="driver-photo" />\n              <div class="driver-info">\n                  <div class="title">\n                      <div class="driver-position">${i.position}</div>\n                      <div class="driver-name">${i.firstName} ${i.lastName}</div>\n                  </div>\n                  <div class="description">\n                    <div class="column">\n                      <div>Races: <span>${i.races}</span></div>\n                      <div>Pole-positions: <span>${i.polePositions}</span></div>\n                      <div>Podiums: <span>${i.podiums}</span></div>\n                    </div>\n                    <div class="column--last">\n                      <div>Wins: <span>${i.wins}</span></div>\n                      <div>Date of birth: <span>${d}</span></div>\n                      <div>Place of birth: <span>${i.city}, ${i.country}</span></div>\n                    </div>\n                  </div>\n              </div>\n            </div>\n          </div>\n        `)})};